import styled from 'styled-components'

export const Button = styled.div`
position: fixed;
width: 100%;
left: 95%;
@media (max-width:500px){
    left:86%;
}
bottom: 50px;
height: 20px;
font-size: 3rem;
z-index: 999;
cursor: pointer;
color: #21fccd;
`
